import React, { useState,Component } from 'react'
import { Text, View, StyleSheet, TextInput, TouchableOpacity ,Image} from 'react-native'
import { auth } from '../firebase-config'
import UserPermissions from '../utilities/UserPermission';
import { Ionicons } from '@expo/vector-icons';
import * as ImagePicker from "expo-image-picker";
import { createUserWithEmailAndPassword } from 'firebase/auth'
import { addDoc, collection, doc} from 'firebase/firestore'

export default class RegisterScreen extends Component {
    
    state = {
        firstname: "",
        lastname: "",
        email: "",
        password: "",
        //avatar:null,
        errorMessage: null
    };
    

    handleSignUp = async (firstname, lastname, email, password) => {
        //let remoteUri = null;
        createUserWithEmailAndPassword(auth, firstname, lastname, email, password);
            let db = this.firestore.collection("users").doc(this.uid);
            db.set({
                firstname: this.state.firstname,
                lastname: this.state.lastname,
                email: this.state.email,
                //avatar: null
            });

            /* if (user.avatar) {
                remoteUri = await this.uploadPhotoAsync(user.avatar, `avatars/${this.uid}`);
                db.set({ avatar: remoteUri }, { merge: true });
            } */

    };


    /* handlePickAvatar = async () => {
        UserPermissions.getCameraPermission();
        let result = await ImagePicker.launchImageLibraryAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.Images,
            allowsEditing: true,
            aspect: [4, 3]   //4:3 , 16:9, 16:10, 1:1
        });

        if (!result.cancelled) {
            this.setState({ user: { ...this.state.user, avatar: result.uri } });
        }
    }; */



    render() {
        return (
            <View style={styles.container}>
                <View>
                    {this.state.errorMessage && <Text style={StyleSheet.error}>{this.state.errorMessage}</Text>}
                </View><br />

                {/* <TouchableOpacity  style={{ alignItems: "center" }} onPress={this.handlePickAvatar}>
                <Image  source={{ uri: this.state.user.avatar }} style={styles.avatar} />
                <Ionicons 
                        name="ios-add"
                        size={80}
                        color="#999"
                    />
                </TouchableOpacity>             <br/><br/><br/> */}

                <View style={{ alignItems: "left", justifyContent: "center" }}>
                    <Text >Firstname</Text>
                    <TextInput
                        style={styles.input}
                        onChangeText={firstname => this.setState({ user:{...this.state.user,firstname} })}
                        value={this.state.firstname} />
                </View>                         <br/>

                <View style={{ alignItems: "left", justifyContent: "center" }}>
                    <Text >Lastname</Text>
                    <TextInput
                        style={styles.input}
                        onChangeText={lastname => this.setState({ user:{...this.state.user,lastname} })}
                        value={this.state.lastname} />
                </View>                         <br/>

                <View style={{ alignItems: "left", justifyContent: "center" }}>
                    <Text >Email</Text>
                    <TextInput
                        style={styles.input}
                        onChangeText={email => this.setState({ user:{...this.state.user,email} })}
                        value={this.state.email} />
                </View>                         <br/>

                <View style={{ alignItems: "left", justifyContent: "center" }}>
                    <Text>Password</Text>
                    <TextInput style={styles.input}
                        onChangeText={password => this.setState({ user:{...this.state.user,password} })}
                        value={this.state.password} />
                </View>                         <br /><br /><br />

                <TouchableOpacity style={styles.button} onPress={this.handleSignUp}>
                    <Text style={{ color: '#FFFFFF', fontWeight: 700, fontSize: 24 }} >Signup</Text>
                </TouchableOpacity>             <br />

                <TouchableOpacity onPress={() => this.props.navigation.navigate("Login")}
                    style={{ textAlign: "center" }}>
                    <Text>มีบัญชีอยู่แล้ว? <Text style={{ color: '#999', fontWeight: 700 }}>Login</Text></Text>
                </TouchableOpacity>
                
            </View>
        )
    }
}




const styles = StyleSheet.create({
    container: {
        flex: 1,
        marginHorizontal:50,
    },
    error: {
        color: "#E9446A",
        textAlign: "center",
    },
    input: {
        borderBottomColor: "#8A8F9E",
        borderBottomWidth: StyleSheet.hairlineWidth,
        height: 40,
        fontSize: 15,
        color: "#161F3D"
    },
    button:{
        marginHorizontal:50,
        backgroundColor:"#3260a8",
        borderRadius: 10,
        height: 50,
        alignItems:"center",
        justifyContent:"center",
        marginTop: 30,
    },
    avatar: {
        position: "absolute",
        width: 100,
        height: 100,
        borderRadius: 50
    },
})
