import React, { useState } from "react";
import { auth, db } from "../firebase-config";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { addDoc, collection, doc } from "firebase/firestore";
import { Link, useNavigate } from "react-router-dom";

function Register({ setIsAuth }) {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [firstname, setFirstName] = useState("");
    const [surname, setSurname] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");

    const navigate = useNavigate();

    const registerWithEmailAndPassword = async (fullname, surname, email, password, phoneNumber) => {
        try {
            const res = await createUserWithEmailAndPassword(auth, email, password);
            const user = res.user;
            localStorage.setItem("isAuth", true);
            setIsAuth(true);
            navigate("/");
            await addDoc(collection(db, "users"), {
                uid: user.uid,
                firstname,
                surname,
                phoneNumber,
                authProvider: "local",
                email,
            });
        } catch (err) {
            console.error(err);
            alert(err.message);
        }
    };

    const register = () => {
        if (!firstname) alert("Please enter name");
        registerWithEmailAndPassword(firstname, surname, email, password, phoneNumber);
    };

    return (
        <View style={styles.container}>

            <View style={{ alignItems: "left", justifyContent: "center" }}>
                <Text >Firstname</Text>
                <TextInput
                    style={styles.input}
                    onChange={(e) => setFirstName(e.target.value)}
                    value={firstname} />
            </View>                         <br />

            <View style={{ alignItems: "left", justifyContent: "center" }}>
                <Text >Surname</Text>
                <TextInput
                    style={styles.input}
                    onChange={(e) => setSurname(e.target.value)}
                    value={surname} />
            </View>                       <br />
            
            <View style={{ alignItems: "left", justifyContent: "center" }}>
                <Text >Phone Number</Text>
                <TextInput
                    style={styles.input}
                    onChange={(e) => setPhoneNumber(e.target.value)}
                    value={phoneNumber} />
            </View>                       <br />
            
            <View style={{ alignItems: "left", justifyContent: "center" }}>
                <Text >Email</Text>
                <TextInput
                    style={styles.input}
                    onChange={(e) => setEmail(e.target.value)}
                    value={email} />
            </View>                       <br />

            <View style={{ alignItems: "left", justifyContent: "center" }}>
                <Text >Password</Text>
                <TextInput
                    type="password"
                    onChange={(e) => setPassword(e.target.value)}
                    value={password} />
            </View>                           <br /><br /><br />

            <TouchableOpacity style={styles.button} onPress={this.handleSignUp}>
                <Text style={{ color: '#FFFFFF', fontWeight: 700, fontSize: 24 }} >Signup</Text>
            </TouchableOpacity>             <br />

            <TouchableOpacity onPress={() => this.props.navigation.navigate("Login")}
                style={{ textAlign: "center" }}>
                <Text>มีบัญชีอยู่แล้ว? <Text style={{ color: '#999', fontWeight: 700 }}>Login</Text></Text>
            </TouchableOpacity>

        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        marginHorizontal: 50,
    },
    error: {
        color: "#E9446A",
        textAlign: "center",
    },
    input: {
        borderBottomColor: "#8A8F9E",
        borderBottomWidth: StyleSheet.hairlineWidth,
        height: 40,
        fontSize: 15,
        color: "#161F3D"
    },
    button: {
        marginHorizontal: 50,
        backgroundColor: "#3260a8",
        borderRadius: 10,
        height: 50,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 30,
    },
    avatar: {
        position: "absolute",
        width: 100,
        height: 100,
        borderRadius: 50
    },
})

export default Register;
